package demo;

public class ByteDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte byte_max = Byte.MAX_VALUE;  //得到Byte型的最大值
		byte byte_min = Byte.MIN_VALUE;  //得到Byte型的最小值
		System.out.println("BYTE的最大值： " + byte_max);
		System.out.println("BYTE的最小值： " + byte_min);
		System.out.println("BYTE所占的位数：" + Byte.SIZE);
		System.out.println("BYTE数据类型：" + Byte.TYPE);
	}

}
